import './App.css';
import React, { useState, useRef } from "react";
import { storage } from "./firebase";
import { ref, uploadBytes} from "firebase/storage";
import { Camera } from "react-camera-pro";
import { v4 } from "uuid";
import { fs } from "filesystem";

const Component = () => {
  const camera = useRef(null);
  const [image, setImage] = useState(null);
  // const fs = require('fs');

  const uploadImage = () =>{
    if (image==null) return;
    // Speicherort
    const imageRef = ref(storage,`test1/${image.name + v4()}`);

    const bild = fs.readFileSync(image.path);
    const b64Image = Buffer.from(bild).toString('base64');

    uploadBytes(imageRef,b64Image).then(()=>{
      alert("Bild hochgeladen");
    });
  }

  return (
    <div>
      <Camera ref={camera} aspectRatio={9/16}/>
      <button onClick={() => setImage(camera.current.takePhoto())}>Take photo</button>
      <button onClick={()=> camera.current.switchCamera()}>Switch Camera</button>
      <button onClick={uploadImage}>Upload photo</button>
    </div>
  );
}

export default Component;
