import './App.css';
import React, { useState, useRef } from "react";
import { storage } from "./firebase";
import { ref, uploadBytes} from "firebase/storage";
import { Camera } from "react-camera-pro";
import { v4 } from "uuid";



const Component = () => {
  const [imagefile, setImagefile] = useState(null);

  // Funktion zum Konvertieren der Binärdaten in ein Bild
  const convertToImage = (image) => {
    // Annahme: Die Binärdaten sind im JPEG-Format (MIME-Typ entsprechend anpassen)
    const mimeType = 'image/jpeg';

    // Konvertiere die Binärdaten in ein Blob-Objekt
    const blob = new Blob([image], { type: mimeType });

    // Erstelle eine temporäre URL für das Blob-Objekt und setze sie als Bild-URL
    const imagefile = URL.createObjectURL(blob);
    setImagefile(imagefile);
  };

  const camera = useRef(null);
  const [image, setImage] = useState(null);

  const uploadImage = () =>{
    if (image==null) return;
    // Speicherort
    const imageRef = ref(storage,`test1/${image.name + v4()}`);
    convertToImage(image)
    uploadBytes(imageRef,imagefile).then(()=>{
      alert("Bild hochgeladen");
    });
  }

  return (
    <div>
      <Camera ref={camera} aspectRatio={9/16}/>
      <button onClick={() => setImage(camera.current.takePhoto())}>Take photo</button>
      <button onClick={()=> camera.current.switchCamera()}>Switch Camera</button>
      <button onClick={uploadImage}>Upload photo</button>
      <img src={image} alt='Taken photo'/>
    </div>
  );
}

export default Component;
