import './App.css';
import React, { useState, useRef, useMemo } from "react";
import { storage } from "./firebase";
import { ref, uploadBytes} from "firebase/storage";
import { Camera } from "react-camera-pro";
import { v4 } from "uuid";

function getBase64StrFromUrl(dataUrl) {
  const prefix = "base64,";
  const sliceIndex = dataUrl.indexOf(prefix);
  if (sliceIndex === -1) throw new Error("Expected base64 data URL");
  return dataUrl.slice(sliceIndex + prefix.length);
}

function decode(base64Str) {
  const binString = window.atob(base64Str);
  const size = binString.length;
  const bytes = new Uint8Array(size);
  for (let i = 0; i < size; i++) {
    bytes[i] = binString.charCodeAt(i);
  }
  return bytes;
}


const Component = () => {
  const camera = useRef(null);
  const [image, setImage] = useState("");

  const byteArray = useMemo(
    () => image ? decode(getBase64StrFromUrl(image)) : new Uint8Array(),
    [image],
  );

  const uploadImage = () =>{
    if (image==null) return;
    // Speicherort
    const imageRef = ref(storage,`test1/${image.name + v4()}`);

    uploadBytes(imageRef,image).then(()=>{
      // alert("Bild hochgeladen")
    })
  }

  return (
    <div>
      <Camera ref={camera} aspectRatio={9/16}/>
      <button onClick={() => setImage(camera.current.takePhoto())}>Take photo</button>
      <button onClick={()=> camera.current.switchCamera()}>Switch Camera</button>
      <button onClick={uploadImage}>Upload photo</button>
    </div>
  );
}

export default Component;
